mapper.select:
SELECT CONCERT_HALL_SQ AS concertHallSq, CONCERT_HALL_NAME AS concertHallName, CONCERT_HALL_BASIC_ADDR AS concertHallRoadAddr FROM ( SELECT ROWNUM as rn, CONCERT_HALL_SQ, CONCERT_HALL_NAME, CONCERT_HALL_BASIC_ADDR FROM ( SELECT CONCERT_HALL_SQ, CONCERT_HALL_NAME, CONCERT_HALL_BASIC_ADDR FROM concert_hall
WHERE CONCERT_HALL_NAME like '%'||#{keyword2}||'%' OR CONCERT_HALL_BASIC_ADDR like '%'||#{keyword2}||'%'
ORDER BY CONCERT_HALL_NAME DESC ) t ) WHERE rn BETWEEN ((#{pageNum} -1) * #{amount} + 1) AND #{pageNum} * #{amount}

SELECT show_sq AS showSq, main_image AS mainImage, sub_image AS subImage, TO_CHAR(start_date, 'YYYY.MM.DD') AS startDate, TO_CHAR(end_date, 'YYYY.MM.DD') AS endDate, TO_CHAR(reservation_date, 'YYYY.MM.DD') AS reservationDate, show_name AS showName, cast, director, show_time AS showTime, viewing_age AS viewingAge, show_content AS showContent, category, show_stat AS showStat, concert_hall_sq AS concertHallSq FROM ( SELECT ROWNUM as rn, show_sq, main_image, sub_image, start_date, end_date, reservation_date, show_name, cast, director, show_time, viewing_age, show_content, category, show_stat, concert_hall_sq FROM ( SELECT show_sq, main_image, sub_image, start_date, end_date, reservation_date, show_name, cast, director, show_time, viewing_age, show_content, category, show_stat, concert_hall_sq FROM show WHERE 1 = 1
AND show_stat != 1
AND show_stat = #{showStat}
ORDER BY show_Sq DESC ) t ) WHERE rn BETWEEN ((#{pageNum} -1) * #{amount} + 1) AND #{pageNum} * #{amount}

mapper.update:



			UPDATE show
            SET
                show_stat = #{showStat}
			WHERE
				show_sq = #{showSq}

mapper.select:
SELECT showName, sellUser, buyUser, ticketSeatPrice, hopePrice, ticketSeat, transferDate, startTime, seatClass, transferStat FROM (SELECT ROWNUM as rn, showName, sellUser, buyUser, ticketSeatPrice, hopePrice, ticketSeat, transferDate, startTime, seatClass, transferStat
FROM (SELECT DISTINCT a.transfer_board_Sq, d.show_name AS showName, g.user_id AS sellUser, h.user_id AS buyUser, b.ticket_seat_price AS ticketSeatPrice, a.hope_price AS hopePrice, b.ticket_seat AS ticketSeat, TO_CHAR(a.transfer_date, 'YYYY-MM-DD') AS transferDate, TO_CHAR(c.start_time, 'YYYY-MM-DD') AS startTime, e.seat_class AS seatClass, CASE WHEN a.transfer_stat = 'N' THEN '거래완료' WHEN a.transfer_stat = 'Y' THEN '거래중' END AS transferStat FROM transfer_board a INNER JOIN ticket b ON b.ticket_sq = a.ticket_sq INNER JOIN showing c ON c.showing_sq = b.showing_sq INNER JOIN show d ON d.show_sq = c.show_sq INNER JOIN seat_class e ON e.show_sq = d.show_sq INNER JOIN showing_seat f ON f.seat_class_sq = e.seat_class_sq INNER JOIN users g ON a.sell_user_sq = g.user_sq LEFT JOIN users h ON a.buy_user_sq = h.user_sq WHERE 1 = 1
AND a.transfer_stat = #{keyword2}
ORDER BY a.transfer_board_Sq DESC ) ) WHERE rn BETWEEN ((#{pageNum} -1) * #{amount} + 1) AND #{pageNum} * #{amount}
