mapper.select:
SELECT showsq, mainimage, subimage, startdate, enddate, reservationdate, showname, cast, director, showtime, viewingage, showcontent, category, showstat, concerthallsq FROM ( SELECT ROWNUM AS rn, showsq, mainimage, subimage, startdate, enddate, reservationdate, showname, cast, director, showtime, viewingage, showcontent, category, showstat, concerthallsq FROM ( SELECT show_sq AS showsq, main_image AS mainimage, sub_image AS subimage, TO_CHAR(start_date, 'YYYY.MM.DD') AS startdate, TO_CHAR(end_date, 'YYYY.MM.DD') AS enddate, TO_CHAR(reservation_date, 'YYYY.MM.DD') AS reservationdate, show_name AS showname, cast, director, show_time AS showtime, viewing_age AS viewingage, show_content AS showcontent, category, show_stat AS showstat, concert_hall_sq AS concerthallsq FROM show WHERE category = ${keyword}
AND CONCAT(cast,show_name) like '%'||#{keyword2}||'%'
ORDER BY show_sq DESC ) ) WHERE rn > (#{pageNum}-1) * #{amount} AND rn <= #{pageNum} * #{amount}
